import { StructuresHighchartsService } from '../../shared/services';

export class JobRangeModelChartService {
  static getRangeOptions(locale, currencyCode, controlPointDisplay, rate) {
    return {
      chart: {
        inverted: true,
        animation: false,
        title: 'Job Ranges',
        currency: currencyCode,
        locale: locale,
        style: {
          fontFamily: `-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, "Helvetica Neue", Arial, sans-serif,
          "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol", "Noto Color Emoji"`
        }
      },
      credits: {
        enabled: false
      },
      title: {
        text: undefined
      },
      legend: {
        useHtml: true,
        layout: 'vertical',
        align: 'right',
        verticalAlign: 'top',
        floating: true,
        y: 30,
        borderWidth: 1
      },
      tooltip: {
        useHTML: true,
        snap: 0,
        backgroundColor: '#000000',
        borderWidth: 0
      },
      yAxis: {
        labels: {
          formatter: function() {
            return StructuresHighchartsService.formatYAxisLabel(this.value, locale, currencyCode, rate);
          }
        },
        opposite: true,
        gridLineWidth: 1,
        gridLineDashStyle: 'Dash',
        lineWidth: 0,
        tickPixelInterval: 200,
        title: {
          text: undefined
        }
      },
      xAxis: {
        visible: false
      },
      plotOptions: {
        columnrange: {
          borderRadius: 5
        },
        series: {
          stickyTracking: false,
          groupPadding: 0,
          pointPadding: 0.1,
          borderWidth: 0,
          marker: {
            states: {
              hover: {
                enabled: false
              }
            }
          }
        }
      },
      series: [{
        name: 'Salary range',
        type: 'columnrange',
        animation: false,
        color: 'rgba(36,134,210,0.45)',
        enableMouseTracking: false
      }, {
        name: 'Mid-point',
        type: 'scatter',
        marker: {
          symbol: 'vline',
          lineWidth: 2,
          lineColor: '#CD8C01',
          radius: 20
        },
        enableMouseTracking: true,
        tooltip: {
          backgroundColor: '#000000',
          useHTML: true,
          padding: 0,
          headerFormat: '<div style="display: inline-block; background-color: black; color: white">',
          pointFormat: '<div><b>{point.jobTitle}</b></div><div>' +
            '<div>{point.midPoint}</div>' +
            '<div>{point.currentMidPoint}</div>' +
            '<div>{point.newMidPoint}</div>' +
            '<div><span style="font-size:25px; color:{point.iconColor};">{point.icon}</span>{point.delta}</div>',
          footerFormat: '</div>'
        }
      }, {
        name: 'Average ' + controlPointDisplay ,
        type: 'scatter',
        marker: {
          symbol: 'vline',
          lineWidth: 2,
          lineColor: '#6236FF',
          radius: 20
        },
        enableMouseTracking: true,
        tooltip: {
          backgroundColor: '#000000',
          useHTML: true,
          padding: 0,
          headerFormat: '<div style="display: inline-block; background-color: black">',
          pointFormat: '<div style="color: white; font-weight: bold">{point.jobTitle}</div>' +
            '<div style="color: white">{point.avgSalary}</div>' +
            '<div style="color: white">Average Comparatio: {point.avgComparatio}%</div>' +
            '<div style="color: white">Average Position in range: {point.avgPositioninRange}%</div>',
          footerFormat: '</div>'
        }
      }, {
        name: 'Outlier ' + controlPointDisplay,
        type: 'scatter',
        dataLabels: {
          useHTML: true,
          enabled: true,
          formatter: function () {
            const fontSize = this.point.count > 99 ? 65 : 90;
            return `<span style="color: #fff; font-size: ${fontSize}%;">${this.point.count}</div>`;
          },
          defer: false,
          x: -0.75,
          y: 11.5
        },
        enableMouseTracking: true,
        marker: {
          enabled: true,
          // tslint:disable-next-line:max-line-length
          symbol: `url(data:image/svg+xml;base64,)`,
        },
        tooltip: {
          backgroundColor: '#000000',
          useHTML: true,
          padding: 0,
          headerFormat: '<div style="display: inline-block; background-color: black">',
          pointFormat: '<div style="color: red">Employees out of Range</div>' +
            '<div style="color: white">{point.countString}<br />{point.avgSalary}</div>' +
            '<div style="color: white">{point.delta}</div>',
          footerFormat: '</div>'
        }
      }]
    };
  }
}
